const d = document;

//State
const state = {
  todoList: [],
};

//Template UI
const template = () => {
  if (template.data.todoList.length < 1)
    return `<p><em>Lista sin tareas por hacer.</em></p>`;

  let todos = template.data.todoList.map((item) => `<li>${item}</li>`).join("");

  return todos;
};

//Add state to the template generated by the UI component
template.data = {
  todoList: [],
};

//Render UI
const render = () => {
  console.log("Local State", template.data);
  console.log("Global State", state); //Not change
  const $list = d.getElementById("todo-list");
  if (!$list) return;

  $list.innerHTML = template();
};

//Reactive state update
const setState = (obj) => {
  for (let key in obj) {
    if (template.data.hasOwnProperty(key)) {
      template.data[key] = obj[key];
    }
  }

  render();
};

//Get inmutable copy state
const getState = () => JSON.parse(JSON.stringify(template.data));

d.addEventListener("DOMContentLoaded", render);

//Setting defaults values state
setState({
  todoList: ["Task 1", "Task 2", "Task 3"],
});

d.addEventListener("submit", (e) => {
  if (!e.target.matches("#todo-form")) return false;

  e.preventDefault();

  const $item = d.getElementById("todo-item");
  if (!$item) return;

  //Reactive state update
  const lastState = getState();
  lastState.todoList.push($item.value);
  setState({ todoList: lastState.todoList });

  //Clear input
  $item.value = "";
  $item.focus;
});
